mapper.select:
SELECT
`appointmentID`,
`doctorID`,
`patientID`,
`date`,
`time`,
`status`,
`appointmentType`,
`updatedBy`,
`updatedOn`,
`createdBy`,
`createdOn`
FROM `doctor_feed`.`appointment`
WHERE 1 = 1

	AND appointmentID = #{appointmentID}


	AND patientID = #{patientID}


	AND doctorID = #{doctorID}


	AND date = #{date}


	ORDER BY ${sortColumn} ${sortOrder}
	LIMIT #{limit} OFFSET #{offset}

SELECT app.`appointmentID`, app.`doctorID`, app.`patientID`, app.`date`, app.`time`, app.`status`, app.`appointmentType`, app.`updatedBy`, app.`updatedOn`, app.`createdBy`, app.`createdOn`, pup.firstName AS firstName, pup.lastName AS lastName, pup.address AS address, pup.contactNo AS contactNo, pup.dateOfBirth AS dateOfBirth, pup.sex AS sex, p.occupation AS occupation FROM `doctor_feed`.`appointment` app JOIN doctor d ON app.doctorID = d.doctorID JOIN patient p ON app.patientID = p.patientID JOIN `doctor_feed`.`bottom_up_user` pu ON p.userID = pu.userID JOIN bottom_up_user_profile pup ON pu.userID = pup.userID WHERE 1 = 1
AND app.appointmentID = #{appointmentID}
AND app.patientID = #{patientID}
AND app.doctorID = #{doctorID}
AND app.date = #{date}
AND app.date IN BETWEEN (DATE(#{startDate}), DATE(#{endDate}))
ORDER BY ${sortColumn} ${sortOrder} LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM `doctor_feed`.`appointment`
WHERE 1 = 1

	AND appointmentID = #{appointmentID}


	AND patientID = #{patientID}


	AND doctorID = #{doctorID}


	AND date = #{date}


	ORDER BY ${sortColumn} ${sortOrder}
	LIMIT #{limit} OFFSET #{offset}
