mapper.select:
SELECT DDS.`drugSettingID`, DDS.`doctorID`, DDS.`drugID`, DDS.`doseTypeCode`, DDS.`drugDoseUnit`, DDS.`drugWhenID`, DDS.`drugAdviceID`, CD.drugName, CD.strength AS drugStrength, CDT.initial AS drugTypeInitial, CDT.name AS drugTypeName, CDA.bangla AS drugAdviceName, CWT.bangla AS drugWhenName, DT.bangla AS doseTypeBngName, DT.english AS doseTypeEngName, DDS.`updatedBy`, DDS.`updatedOn`, DDS.`createdBy`, DDS.`createdOn` FROM doctor_feed.`doctor_drug_setting` DDS JOIN doctor_feed.content_drug CD ON DDS.drugID = CD.drugID JOIN doctor_feed.content_drug_type CDT ON CD.typeID = CDT.drugTypeID JOIN doctor_feed.content_dose_type DT ON DDS.doseTypeCode = DT.doseCode LEFT JOIN doctor_feed.content_drug_advice CDA ON DDS.drugAdviceID = CDA.drugAdviceID LEFT JOIN doctor_feed.content_when_type CWT ON DDS.drugWhenID = CWT.whenTypeID WHERE 1 = 1
AND doctorID = #{doctorID}
AND drugID = #{drugID}
AND drugSettingID = #{drugSettingID}

SELECT COUNT(drugSettingID)
FROM `doctor_drug_setting`
WHERE 1 = 1

	AND doctorID = #{doctorID}


	AND drugID = #{drugID}

mapper.delete:
DELETE  FROM doctor_drug_setting
WHERE 1=1

	AND drugSettingID = #{drugSettingID}
