mapper.select:
SELECT PD.`drugSettingID`, PD.`drugID`, PD.`doctorID`, PD.`drugTypeID`, PD.`doseTypeCode`, PD.`drugDoseUnit`, PD.`drugWhenID`, PD.`drugAdviceID`, PD.`doseString`, CD.genericDrugName AS drugName, CDT.initial AS drugTypeInitial, CDT.name AS drugTypeName, CDA.bangla AS drugAdviceName, CWT.bangla AS drugWhenName, DT.bangla AS doseTypeBngName, DT.english AS doseTypeEngName, PD.`updatedBy`, PD.`updatedOn`, PD.`createdBy`, PD.`createdOn` FROM doctor_feed.drug_default_setup PD JOIN doctor_feed.content_drug_generic CD ON PD.drugID = CD.genericID JOIN doctor_feed.content_drug_type CDT ON CD.typeID = CDT.drugTypeID JOIN doctor_feed.content_dose_type DT ON PD.doseTypeCode = DT.doseCode LEFT JOIN doctor_feed.content_drug_advice CDA ON PD.drugAdviceID = CDA.drugAdviceID LEFT JOIN doctor_feed.content_when_type CWT ON PD.drugWhenID = CWT.whenTypeID WHERE 1 = 1
AND PD.drugSettingID = #{drugSettingID}
AND PD.drugID = #{drugID}
AND PD.doctorID = #{doctorID}
ORDER BY ${sortColumn} ${sortOrder} LIMIT #{limit} OFFSET #{offset}

mapper.delete:
DELETE  FROM drug_default_setup
WHERE 1=1

	AND drugSettingID = #{drugSettingID}
