mapper.select:
SELECT ep.`permissionID`, ep.`companyModuleID`, ecm.`shortName` AS companyModuleName, ep.`functionCode`, ep.`shortName`, ep.`isUserDefined`, ep.`updatedOn`, ep.`updatedBy`, ep.`createdOn`, ep.`createdBy` FROM `bottom_up_permission` ep JOIN bottom_up_company_module ecm ON ecm.companyModuleID = ep.companyModuleID JOIN bottom_up_company ec ON ecm.companyID = ec.companyID WHERE 1 = 1
AND ep.permissionID = #{permissionID}
AND ec.companyID = #{companyID}
AND ep.isUserDefined = #{isUserDefined}

SELECT ep.`permissionID`, ep.`companyModuleID`, ep.`functionCode`, ep.`shortName`, ep.`isUserDefined`, ep.`updatedOn`, ep.`updatedBy`, ep.`createdOn`, ep.`createdBy`, CASE WHEN egp.groupPermissionID IS NOT NULL THEN true ELSE false END AS isAssigned FROM `bottom_up_permission` ep JOIN bottom_up_company_module ecm ON ecm.companyModuleID = ep.companyModuleID LEFT JOIN bottom_up_group_permission egp ON ep.permissionID = egp.permissionID AND egp.userGroupID = #{userGroupID} WHERE 1 = 1
AND ep.isUserDefined = #{isUserDefined}
AND ecm.companyID = #{companyID}
