mapper.insert:
INSERT INTO `prescription_advice`( `appointmentID`, `adviceID`, `updatedBy`, `updatedOn`, `createdBy`, `createdOn` ) SELECT #{newAppointmentID}, pa.`adviceID`, #{userName}, NOW(), #{userName}, NOW() FROM prescription_advice pa WHERE 1=1
AND pa.appointmentID = #{appointmentID}
AND pa.presAdviceID = #{presAdviceID}

mapper.select:
SELECT pa.`presAdviceID`, pa.`appointmentID`, pa.`adviceID`, ca.advice, ca.lang, pa.`updatedBy`, pa.`updatedOn`, pa.`createdBy`, pa.`createdOn` FROM doctor_feed.`prescription_advice` pa JOIN doctor_feed.content_advice ca ON pa.adviceID = ca.adviceID WHERE 1 = 1
AND presAdviceID = #{presAdviceID}
AND pa.appointmentID = #{appointmentID}
ORDER BY ${sortColumn} ${sortOrder} LIMIT #{limit} OFFSET #{offset}

mapper.delete:
DELETE  FROM prescription_advice
WHERE 1=1

	AND presAdviceID = #{presAdviceID}
