mapper.select:
SELECT PDD.`presDrugID`, PDD.`dose`, PDD.`numOfDay`, PDD.`durationType`, CDT.bangla as bngDurationName, CDT.english as engDurationName, CDT.pdf as pdfDurationName, PDD.`updatedBy`, PDD.`updatedOn`, PDD.`createdBy`, PDD.`createdOn` FROM doctor_feed.`prescription_drug_dose` PDD JOIN doctor_feed.content_duration_type CDT ON PDD.durationType = CDT.durationType WHERE 1 = 1
AND presDrugID = #{presDrugID}
ORDER BY ${sortColumn} ${sortOrder} LIMIT #{limit} OFFSET #{offset}

mapper.delete:
DELETE  FROM prescription_drug_dose
WHERE 1=1

	AND presDrugID = #{presDrugID}
