mapper.select:
SELECT PD.`presDrugID`, PD.`appointmentID`, PD.`drugTypeID`, PD.`drugID`, PD.`drugTimeID`, PD.`drugDoseUnit`, PD.`drugWhenID`, PD.`drugAdviceID`, PD.`presNum`, CD.drugName, CD.strength AS drugStrength, CDT.initial AS drugTypeInitial, CDT.name AS drugTypeName, CDA.bangla AS drugAdviceName, CWT.bangla AS drugWhenName, PD.`updatedBy`, PD.`updatedOn`, PD.`createdBy`, PD.`createdOn` FROM doctor_feed.`prescription_drug` PD JOIN doctor_feed.content_drug CD ON PD.drugID = CD.drugID JOIN doctor_feed.content_drug_type CDT ON CD.typeID = CDT.drugTypeID LEFT JOIN doctor_feed.content_drug_advice CDA ON PD.drugAdviceID = CDA.drugAdviceID LEFT JOIN doctor_feed.content_when_type CWT ON PD.drugWhenID = CWT.whenTypeID WHERE 1 = 1
AND presDrugID = #{presDrugID}
AND appointmentID = #{appointmentID}
ORDER BY ${sortColumn} ${sortOrder} LIMIT #{limit} OFFSET #{offset}

mapper.delete:
DELETE  FROM prescription_drug
WHERE 1=1

	AND presDrugID = #{presDrugID}
