mapper.select:
SELECT pfd.`presFamilyDiseaseID`, pfd.`appointmentID`, pfd.`familyDiseaseID`, pfh.diseaseID, pfh.relation, cr.relationName, pfh.present, pfh.type, pfh.detail, cd.diseaseName, pfd.`updatedBy`, pfd.`updatedOn`, pfd.`createdBy`, pfd.`createdOn` FROM doctor_feed.`prescription_family_disease` pfd JOIN doctor_feed.patient_family_history pfh ON pfd.familyDiseaseID = pfh.familyHistoryID JOIN doctor_feed.content_relation cr ON pfh.relation = cr.relationID JOIN doctor_feed.content_disease cd ON pfh.diseaseID = cd.diseaseID WHERE 1 = 1
AND pfd.presFamilyDiseaseID = #{presFamilyDiseaseID}
AND pfd.appointmentID = #{appointmentID}
ORDER BY ${sortColumn} ${sortOrder} LIMIT #{limit} OFFSET #{offset}

mapper.delete:
DELETE  FROM prescription_family_disease
WHERE 1=1

	AND presFamilyDiseaseID = #{presFamilyDiseaseID}
