mapper.select:
SELECT ph.`presHistoryID`, ph.`appointmentID`, ph.`patientHistoryID`, h.typeCode AS typeCode, h.name AS historyName, h.shortName historyShortName, pathis.historyResult AS historyResult, ph.`updatedBy`, ph.`updatedOn`, ph.`createdBy`, ph.`createdOn` FROM doctor_feed.`prescription_history` ph JOIN doctor_feed.patient_history pathis ON ph.patientHistoryID = pathis.patientHistoryID JOIN doctor_feed.history h ON pathis.historyID = h.historyID WHERE 1 = 1
AND ph.presHistoryID = #{presHistoryID}
AND pathis.patientID = #{patientID}
AND ph.appointmentID = #{appointmentID}
AND h.typeCode = #{typeCode}
ORDER BY ${sortColumn} ${sortOrder} LIMIT #{limit} OFFSET #{offset}

mapper.delete:
DELETE  FROM prescription_history
WHERE 1=1

	AND presHistoryID = #{presHistoryID}


	AND   appointmentID = #{appointmentID}
