mapper.select:
SELECT pd.`presPastDiseaseID`, pd.`appointmentID`, pd.`pastDiseaseID`, cd.diseaseName AS diseaseName, pd.`updatedBy`, pd.`updatedOn`, pd.`createdBy`, pd.`createdOn` FROM doctor_feed.`prescription_past_disease` pd JOIN doctor_feed.content_disease cd ON pd.pastDiseaseID = cd.diseaseID WHERE 1 = 1
AND presPastDiseaseID = #{presPastDiseaseID}
AND pd.appointmentID = #{appointmentID}
ORDER BY ${sortColumn} ${sortOrder} LIMIT #{limit} OFFSET #{offset}

mapper.delete:
DELETE  FROM prescription_past_disease
WHERE 1=1

	AND presPastDiseaseID = #{presPastDiseaseID}
