mapper.select:
SELECT pr.`prescriptionReferenceID`, pr.`appointmentID`, pr.`refferedDoctorID`, rd.doctorName, rd.doctorAddress, pr.`updatedBy`, pr.`updatedOn`, pr.`createdBy`, pr.`createdOn` FROM `prescription_reference` pr JOIN doctor_feed.referred_doctor rd ON pr.refferedDoctorID = rd.referredDoctorID WHERE 1 = 1
AND prescriptionReferenceID = #{prescriptionReferenceID}
AND pr.appointmentID = #{appointmentID}
ORDER BY ${sortColumn} ${sortOrder} LIMIT #{limit} OFFSET #{offset}

mapper.delete:
DELETE  FROM prescription_reference
WHERE 1=1

	AND prescriptionReferenceID = #{prescriptionReferenceID}
